import React, { useState } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Grid, TextField, Typography } from '@mui/material';
import { Button } from '@mui/material';
import backgroundImage from '../../../public/background.jpg';

const LoginPage = () => {
  const navigate = useNavigate();
  const [error, setError] = useState('');

  const handleSubmit = async (values : any, { setSubmitting } : any) => {
    // Simulate basic login (without any validation)
    // In a real application, this should communicate with a backend for authentication
    if (values.username !== '' && values.password !== '') {
      // No validation in this example, direct navigation to '/home'
      navigate('/home');
    } else {
      setError('Please fill in all fields.');
    }
    setSubmitting(false);
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: `url(${backgroundImage})`,
      }}
    >
      <Container
        maxWidth="xs"
        style={{
          padding: '35px',
          background: 'white',
          borderRadius: '10px',
        }}
      >
        <Typography variant="h4" align="center" gutterBottom>
          Login
        </Typography>
        <Formik
          initialValues={{ username: '', password: '' }}
          enableReinitialize
          validate={(values : any) : any => {
            const errors: { username?: string; password?: string } = {};

            if (!values.username) {
              errors.username = 'Required';
            } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.username)) {
              errors.username = 'Invalid username address';
            }

            if (!values.password) {
              errors.password = 'Required';
            }

            return errors;
          }}
          onSubmit={handleSubmit}
        >
          {({ isSubmitting, isValid, touched } : any) => (
            <Form>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <Field
                    type="username"
                    name="username"
                    as={TextField}
                    label="username"
                    fullWidth
                    variant="outlined"
                    error={touched.username && !isValid && !isSubmitting}
                    helperText={<ErrorMessage name="username" component="div" />}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Field
                    type="password"
                    name="password"
                    as={TextField}
                    label="Password"
                    fullWidth
                    variant="outlined"
                    error={touched.password && !isValid && !isSubmitting}
                    helperText={<ErrorMessage name="password" component="div" />}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Button
                    color="inherit"
                    type="submit"
                    variant="contained"
                    fullWidth
                    style={{
                      textDecoration: 'none',
                      fontSize: '18px',
                      color: 'white',
                      backgroundColor: 'grey',
                    }}
                  >
                    Submit
                  </Button>
                </Grid>
              </Grid>
            </Form>
          )}
        </Formik>
        {error && (
          <Typography variant="body2" color="error" align="center">
            {error}
          </Typography>
        )}
      </Container>
    </div>
  );
};

export default LoginPage;
