// CarDetailsPage.tsx
import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { Car } from '../Atoms/Carmodel';

interface CarDetailsProps {
  carData: Car[];
  addToCart: (car: Car) => void;
}

const CarDetailsPage: React.FC<CarDetailsProps> = ({ carData, addToCart }) => {
  const params: { id: string } = useParams() as { id: string }; // ID-Parameter aus der URL

  const selectedCar = carData.find(car => car.id === parseInt(params.id, 10));

  if (!selectedCar) {
    return <div>Auto nicht gefunden</div>;
  }

  const handleAddToCart = () => {
    addToCart(selectedCar); // Fügt das ausgewählte Auto dem Warenkorb hinzu
  };

  return (
    <div>
      <h1>{selectedCar.brand} {selectedCar.model}</h1>
      <p>Category: {selectedCar.category}</p>
      <p>Seats: {selectedCar.seats}</p>
      <p>Horsepower: {selectedCar.horsepower}</p>
      <p>Seats Count: {selectedCar.seatsCount}</p>
      <p>Fuel Type: {selectedCar.fuelType}</p>
      <p>Price: {selectedCar.price}</p>
      <img src={selectedCar.link} alt={`${selectedCar.brand} ${selectedCar.model}`} />

      <button onClick={handleAddToCart}>In den Warenkorb hinzufügen</button>
      <Link to="/cars">Zurück zur CarPage</Link>
      <Link to="/payment">Zur Zahlungsseite</Link>
    </div>
  );
};

export default CarDetailsPage;
