import { Car } from "./Carmodel";

const { MongoClient } = require('mongodb');

const uri = "mongodb+srv://user:SzfI2Oi7HddXdl6b@car.h9rsz6v.mongodb.net/?retryWrites=true&w=majority&appName=car";

const client = new MongoClient(uri, {
  useNewUrlParser: true,
  useUnifiedTopology: true
});

export function getCars(): Promise<Car[]> {
    try {
        client.connect();
    
        const database = client.db('car');
        const collection = database.collection('cars');
    
        // Find all cars
        const cars = collection.find({}).toArray();
    
        // Return the cars array
        return cars;
      } finally {
        client.close();
      }
    }
