import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AppBar as MuiAppBar, Toolbar, Button, Box } from '@mui/material';

const Navbar: React.FC = () => {
  const location = useLocation();
  const hideNavbar = location.pathname === '/' || location.pathname === '/register';

  if (hideNavbar) {
    return null; // Do not render the Navbar on the Login and Register pages
  }

  return (
    <MuiAppBar
      position="static"
      style={{
        height: '80px',
        background: 'black',
      }}
    >
      <Toolbar>
        <Box
          display="flex"
          justifyContent="space-between"
          width="100%"
          alignItems="center"
        >
          <Button
            color="inherit"
            component={Link}
            to="/payment"
            style={{
              textDecoration: 'none',
              fontSize: '18px',
              color: 'white',
            }}
          >
            Home
          </Button><Button
            color="inherit"
            component={Link}
            to="/cars"
            style={{
              textDecoration: 'none',
              fontSize: '18px',
              color: 'white',
            }}
          >
            Cars
          </Button>
          <Button>
          <Link to="/home" style={{ textDecoration: "none" }}>
              <div
                style={{
                  fontSize: "27px",
                  fontWeight: "bold",
                  fontFamily: "Lobster, sans-serif",
                  color: "white", // Change "Car Rental" text color to white
                }}
              >
                Car Rental
              </div>
            </Link>
          </Button>
          <Button
            color="inherit"
            component={Link}
            to="/payment"
            style={{
              textDecoration: 'none',
              fontSize: '18px',
              color: 'white',
            }}
          >
            Cart
          </Button>
          <Button
            color="inherit"
            component={Link}
            to="/about"
            style={{
              textDecoration: 'none',
              fontSize: '18px',
              color: 'white',
            }}
          >
            About Us
          </Button>
        </Box>
      </Toolbar>
    </MuiAppBar>
  );
};

export default Navbar;
