import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const LoginPage: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    // Simulate basic login (without any validation)
    // In a real application, this should communicate with a backend for authentication
    if (username !== '' && password !== '') {
      // No validation in this example, direct navigation to '/home'
      navigate('/home');
    } else {
      setError('Bitte füllen Sie alle Felder aus.');
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <div>
          <label>Benutzername:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label>Passwort:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Einloggen</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>

      <div>
        {/* Link zur Registrierungsseite */}
        <p>Noch keinen Account? <Link to="/register">Registrieren</Link></p>
      </div>
    </div>
  );
};

export default LoginPage;
