import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { Car } from '../Atoms/Carmodel';
import { Button, Typography, Card, CardContent, CardActions } from '@mui/material';
import backgroundImage from '../../background.jpg';

interface CarDetailsProps {
  carData: Car[];
  addToCart: (car: Car) => void;
}

const CarDetailsPage: React.FC<CarDetailsProps> = ({ carData, addToCart }) => {
  const params: { id: string } = useParams() as { id: string };

  const selectedCar = carData.find((car) => car.id === parseInt(params.id, 10));

  if (!selectedCar) {
    return <div>Auto nicht gefunden</div>;
  }

  const handleAddToCart = () => {
    addToCart(selectedCar);
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: `url(${backgroundImage})`,
      }}
    >
      <Card style={{ maxWidth: '600px', marginBottom: '20px', background: 'rgba(255, 255, 255, 0.9)' }}>
        <CardContent>
          <Typography variant="h4" component="div" gutterBottom>
            {selectedCar.brand} {selectedCar.model}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Category: {selectedCar.category}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Seats: {selectedCar.seats}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Horsepower: {selectedCar.horsepower}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Seats Count: {selectedCar.seatsCount}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Fuel Type: {selectedCar.fuelType}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Price: {selectedCar.price}
          </Typography>
          <img
            src={selectedCar.link}
            alt={`${selectedCar.brand} ${selectedCar.model}`}
            style={{ width: '100%', marginTop: '20px', borderRadius: '8px' }}
          />
        </CardContent>
        <CardActions>
          <Button onClick={handleAddToCart} variant="contained" color="primary">
            In den Warenkorb hinzuf√ºgen
          </Button>
        </CardActions>
      </Card>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Link to="/cars" style={{ textDecoration: 'none', margin: '10px' }}>
          <Button variant="outlined" color="primary">
            Zur√ºck zur CarPage
          </Button>
        </Link>
        <Link to="/payment" style={{ textDecoration: 'none', margin: '10px' }}>
          <Button variant="outlined" color="primary">
            Zur Zahlungsseite
          </Button>
        </Link>
      </div>
    </div>
  );
};

export default CarDetailsPage;
