import React, { useState, useEffect } from 'react';
import carData from '../Atoms/Carmodel';
import backgroundImage from '../../background.jpg';
import Navbar from '../Molecules/Navbar'; // Assuming you have a Navbar component
import { Fab, useScrollTrigger, Zoom } from '@mui/material';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

const ScrollTop: React.FC = ({ children } : any) => {
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 100,
  });

  const handleClick = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  return (
    <Zoom in={trigger}>
      <div onClick={handleClick} role="presentation" style={{ position: 'fixed', bottom: 16, right: 16 }}>
        {children}
      </div>
    </Zoom>
  );
};

const HomePage: React.FC = () => {
  const [index, setIndex] = useState(0);
  const [delay, setDelay] = useState(3000); // Delay between images in milliseconds

  const nextSlide = () => {
    setIndex((prevIndex) => (prevIndex === carData.length - 1 ? 0 : prevIndex + 1));
  };

  useEffect(() => {
    const interval = setInterval(nextSlide, delay);
    return () => clearInterval(interval);
  }, [index]);

  const fixedImageHeight = '400px'; // Set your desired fixed height for the images

  return (
    <div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minHeight: '100vh',
          background: `url(${backgroundImage})`, // Assuming backgroundImage is defined
          backgroundSize: 'cover',
          position: 'relative',
        }}
      >
        <div
          style={{
            padding: '20px',
            borderRadius: '10px',
            maxWidth: '800px',
            width: '100%',
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
          }}
        >
          <div className="slideshow" style={{ position: 'relative' }}>
            {carData.map((car, carIndex) => (
              <div
                key={car.id}
                style={{
                  display: carIndex === index ? 'block' : 'none',
                  textAlign: 'center',
                }}
              >
                <p style={{ color: 'white', fontSize: '24px', fontWeight: 'bold' }}>
                  {car.brand} {car.model}
                </p>
                <img
                  src={car.link}
                  alt={`${car.brand} ${car.model}`}
                  style={{ width: '100%', height: fixedImageHeight, borderRadius: '10px' }}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
      <ScrollTop>
        <Fab color="primary" size="small" aria-label="scroll back to top">
          <KeyboardArrowUpIcon />
        </Fab>
      </ScrollTop>
    </div>
  );
};

export default HomePage;
