import React, { useState, useEffect } from 'react';
import carData from '../Atoms/Carmodel'; // Stelle sicher, dass der Pfad zur Carmodel-Datei korrekt ist

const HomePage: React.FC = () => {
  const [index, setIndex] = useState(0);
  const [delay, setDelay] = useState(3000); // Verzögerung zwischen den Bildern in Millisekunden

  const nextSlide = () => {
    setIndex((prevIndex) => (prevIndex === carData.length - 1 ? 0 : prevIndex + 1));
  };

  useEffect(() => {
    const interval = setInterval(nextSlide, delay);
    return () => clearInterval(interval);
  }, [index]);

  return (
    <div>
      <h1>Willkommen zur Diashow der Autos</h1>
      <div className="slideshow">
        {carData.map((car, carIndex) => (
          <div
            key={car.id}
            style={{
              display: carIndex === index ? 'block' : 'none'
            }}
          >
            <img src={car.link} alt={`${car.brand} ${car.model}`} />
            <p>
              {car.brand} {car.model}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
