import React from 'react';
import { Facebook, Twitter, Instagram } from '@mui/icons-material';
import backgroundImage from '../../background.jpg';

const AboutUsPage: React.FC = () => {
  return (
    <div
      style={{
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
      }}
    >
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          zIndex: -1,
          backdropFilter: 'blur(0.2em)',
        }}
      ></div>
      <div
        style={{
          zIndex: 1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          padding: '20px',
          borderRadius: '10px',
          maxWidth: '800px', // Adjust the width based on your design
          width: '100%',
          backgroundColor: 'rgba(255, 255, 255, 0.1)',
          color: 'white',
        }}
      >
        <h1 style={{ marginBottom: '20px' }}>About Us</h1>
        <p>Welcome to our company!</p>
        <p>We are dedicated to providing high-quality products/services and ensuring customer satisfaction.</p>
        <p>Contact us at info@company.com</p>
        
        {/* Social Media Icons */}
        <div style={{ marginTop: '20px' }}>
          <Facebook style={{ fontSize: '50px', margin: '0 10px', color: 'white' }} />
          <Twitter style={{ fontSize: '50px', margin: '0 10px', color: 'white' }} />
          <Instagram style={{ fontSize: '50px', margin: '0 10px', color: 'white' }} />
          {/* Add more social media icons as needed */}
        </div>
      </div>
    </div>
  );
};

export default AboutUsPage;
