import React, { useState, useEffect } from "react";
import { useSpring, animated } from "react-spring";

const AnimatedIntro = ({ onIntroComplete }: any) => {
  const text = "Car Rental";
  const [visible, setVisible] = useState(false);

  const animation = useSpring({
    opacity: visible ? 1 : 0,
    from: { opacity: 0 },
    config: { duration: 2000 }, // Adjust the duration for slower appearance
    onRest: () => {
      if (text.length > 0) {
        setVisible(true);
      } else {
        onIntroComplete();
      }
    },
  });

  useEffect(() => {
    let index = 0;
    const timer = setInterval(() => {
      if (index < text.length) {
        index++;
      } else {
        clearInterval(timer);
        setTimeout(() => {
          onIntroComplete();
        }, 2000); // Adjust the duration for slower appearance
      }
    }, 150); // Adjust the speed of letter-by-letter appearance

    return () => clearInterval(timer);
  }, [text, onIntroComplete]);

  return (
    <>
      <style>
        @import url('https://fonts.googleapis.com/css2?family=Lobster&display=swap');
      </style>

      <div
        style={{
          fontFamily: "Lobster, sans-serif", // Use the Lobster font
          backgroundColor: "black",
          width: "100%",
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          position: "absolute",
          fontWeight: "bold",
        }}
      >
        <animated.div
          style={{
            ...animation,
            fontSize: "120px",
            color: "white", // Adjust the font size to make the letters bigger
          }}
        >
          {text.split("").map((letter, index) => (
            <span key={index} style={{ visibility: visible ? "visible" : "hidden" }}>
              {letter}
            </span>
          ))}
        </animated.div>
      </div>
    </>
  );
};

export default AnimatedIntro;
