import React, { useState, useEffect, ReactNode } from 'react';
import { Car } from '../Atoms/Carmodel';
import backgroundImage from '../../background.jpg';
import Navbar from '../Molecules/Navbar';
import { Fab, useScrollTrigger, Zoom } from '@mui/material';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

// ScrollTop component definition
interface ScrollTopProps {
  children: ReactNode;
}

const ScrollTop: React.FC<ScrollTopProps> = ({ children }: ScrollTopProps) => {
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 100,
  });

  const handleClick = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  return (
    <Zoom in={trigger}>
      <div onClick={handleClick} role="presentation" style={{ position: 'fixed', bottom: 16, right: 16 }}>
        {children}
      </div>
    </Zoom>
  );
};

// CarPage component definition
interface CarPageProps {
  carData: Car[];
}

const CarPage: React.FC<CarPageProps> = ({ carData }) => {
  const fixedImageHeight = '400px'; // Set your desired fixed height for the images

  return (
    <div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minHeight: '100vh',
          background: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          position: 'relative',
        }}
      >
        <div
          style={{
            padding: '20px',
            borderRadius: '10px',
            maxWidth: '800px',
            width: '100%',
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
          }}
        >
          <div className="car-list" style={{ position: 'relative' }}>
            {carData.map((car) => (
              <div
                key={car.id}
                style={{
                  textAlign: 'center',
                  marginBottom: '20px',
                }}
              >
                <p style={{ color: 'white', fontSize: '24px', fontWeight: 'bold' }}>
                  {car.brand} {car.model}
                </p>
                <img
                  src={car.link}
                  alt={`${car.brand} ${car.model}`}
                  style={{ width: '100%', height: fixedImageHeight, borderRadius: '10px' }}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
      <ScrollTop>
        <Fab color="primary" size="small" aria-label="scroll back to top">
          <KeyboardArrowUpIcon />
        </Fab>
      </ScrollTop>
    </div>
  );
};

export default CarPage;
