import React, { useEffect, useState } from "react";
import { useSpring, animated } from "react-spring";

const AnimatedIntro = ({ onIntroComplete }: any) => {
  const text = "Car Rental";
  const [visible, setVisible] = useState(true);

  const fadeInOut = useSpring({
    opacity: visible ? 1 : 0,
    from: { opacity: 0 },
    config: { duration: 1000 },
    onRest: () => {
      if (text.length > 0) {
        setVisible(true);
      } else {
        onIntroComplete();
      }
    },
  });

  useEffect(() => {
    let index = 0;
    const timer = setInterval(() => {
      if (index < text.length) {
        index++;
      } else {
        clearInterval(timer);
        setTimeout(() => {
          setVisible(false);
          onIntroComplete();
        }, 200); // Delay after text animation
      }
    }, 0); // Adjust the speed of letter-by-letter appearance

    return () => clearInterval(timer);
  }, [text, onIntroComplete]);

  return (
    <>
      <style>
        @import url("https://fonts.googleapis.com/css2?family=Lobster&display=swap");
      </style>

      <animated.div
        style={{
          ...fadeInOut,
          fontSize: "120px",
          color: "white",
          fontFamily: "Lobster, sans-serif",
          backgroundColor: "black",
          width: "100%",
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          position: "absolute",
          fontWeight: "bold",
        }}
      >
        {text.split("").map((letter, index) => (
          <span key={index} style={{ visibility: visible ? "visible" : "hidden" }}>
            {letter}
          </span>
        ))}
      </animated.div>
    </>
  );
};

export default AnimatedIntro;
